import * as core from '@actions/core'
import styles from 'ansi-styles'

/**
 * Print plain info message w/o color.
 *
 * @param {string} message Message which should be printed to console.
 */
const info = (message: string) => {
  core.info(message)
}

/**
 * Print success message in green color.
 *
 * @param {string} message Message which should be printed to console.
 */
const success = (message: string) => {
  core.info(`${styles.color.green.open}${message}${styles.color.green.close}`)
}

/**
 * Print warning message in yellow color.
 *
 * @param {string} message Message which should be printed to console.
 */
const warning = (message: string) => {
  core.warning(`${styles.color.yellowBright.open}${message}${styles.color.yellowBright.close}`)
}

/**
 * Print error message in red color.
 *
 * @param {string} message Message which should be printed to console.
 */
const error = (message: string) => {
  core.error(`${styles.color.red.open}${message}${styles.color.red.close}`)
}

export {info, success, warning, error}
